/**
 * Braedcrumb By <b><a href="mailto: me@maghrooni.ir">Maghrooni</a></b>
 * 
 * Build Dynamic BreadCrumb Based on Current URL
 * 
 * <li>Set the Options or use default options</li>
 * <li>Example of overriding option: <code>breadcrumb.element = 'ul';</code></li>
 * <li>set write to document option to false if you just want the output string <code>breadcrumb.write = false;</code></li>
 * <li>call the build function <code>breadcrumb.build();</code></li>
 * 
 * @author Maghrooni <me@maghrooni.ir>
 * 
 */
var breadcrumb={main:[{"text":'Main',"link":'/','class':'active'}],element:'ol',class:'breadcrumb',lastClass:'current-bread',write:true,build:function(){var currentUrl=location.href.split('/').slice(3);var breads=[];var output='';for(var i=0;i<this.main.length;i++){var text=this.main[i].text;var link=this.main[i].link;var cls=this.main[i].class;breads.push({"text":text,"link":link,'class':cls});}
for(var i=0;i<currentUrl.length;i++){var text=currentUrl[i];text=text[0].toUpperCase()+text.substring(1);var link='/'+currentUrl.slice(0,i+1).join('/');breads.push({"text":text,"link":link,'class':' '});}
output+='<'+this.element+' class="'+this.class+'">';var breadLen=breads.length;for(var j=0;j<breadLen-1;j++){if(breads[j].text!=''){output+='<li class="'+breads[j].class+'"><a href="'+breads[j].link+'">'+breads[j].text+'</a></li>';}}
output+='<li class="'+this.lastClass+'"><a href="'+breads[breadLen-1].link+'">'+breads[breadLen-1].text+'</a></li>';output+='</'+this.element+'>';if(this.write==true){document.write(output);}
return output;}};